public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
					
		//Without new line
		System.out.print("I love pizza");
		System.out.print("It's really good");
		
		//Generate System.out.println() like this: sysout + Ctrl + Space + Enter
		
		//Alternatives with new line
		System.out.print("\nI love pizza\n");
		System.out.println("It's really good");
		System.out.print("\t I love pizza\n");
		
		//If you want do display a quote
		System.out.println("\"Some Quote\"");
		
		// I you want to use a backslash, you use two backslashes:
		System.out.println("\\Some Quote\\");
		System.out.println();
	}

}
